import React from 'react';

const API_KEY = '63f26d323a9e3545b746bba6364e73b4';

function App() {

  const response = (city, nrDays) => {
    const url = `api.openweathermap.org/data/2.5/forecast/daily?q=${city}&cnt=${nrDays}&appid=${API_KEY}`;
    fetch('https://api.fungenerators.com/yaml/fungenerators.openapi.yaml?v4')
        .then( response => response.json())
        .then( data => console.log(data))
        .catch( err => console.error(err))
  }

  return (
    <h1>{response('Coimbra', 2)}</h1>
  );
}

export default App;
