import React from 'react'
import 'weather-icons/css/weather-icons.css'
import styled from '@emotion/styled'

const Container = styled.div`
    display: inline-flex;
    flex-direction: column;
    align-items: center;
    border: 1px solid;

`

const WeatherIcon = styled.i`
    font-size: 50px;
`

const WeatherMaxMin = styled.div`
    display: flex;
    justify-content: space-between;
    font-size: 20px;
    border: 1px solid;
`

const CardContent = (props) => {
    console.log('inside content', props.weather)
    return (
        <Container>
            <h1>{props.weather}</h1>
            <WeatherIcon className="wi wi-night-sleet"></WeatherIcon>
            <WeatherMaxMin>
                <p>20ºC</p>
                <p>2ºC</p>
            </WeatherMaxMin>
        </Container>
    );
}

export default CardContent
