import React, { useState } from 'react'
import CardContent from "./CardContent";

import styled from '@emotion/styled'

const Title = styled.div`
    display: flex;
    justify-content: center;
    font-size: 40px;
    background-color: #3581B8;
    padding: 20px;
    flex-wrap: wrap;
    color: #EBE9E9;
    text-shadow: 2px 2px #FCB07E;
`

const WeatherInfo = styled.div`
    display: flex;
    justify-content: center;
`

const initialData = {
    title: 'aliens coming',
    temp_max: '0',
    temp_min: '0',
    temp: '0',
}

const Card = () => {
    const [weather, setWeather] = useState(initialData)

    const API_KEY = '63f26d323a9e3545b746bba6364e73b4';
    const response = (city, nrDays) => {
        const url = `http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`;
        fetch(url)
            .then( response => response.json())
            .then( data => {
                console.log(data)
                setWeather(data.weather[0].main);
            })
            .catch( err => console.error('ERROR: ', err))
    }

    return (
        <div>
            <Title>Weather app</Title>
            <WeatherInfo>
                <CardContent weather={weather.title} />
            </WeatherInfo>
            <button onClick={() => response('Coimbra', 7)}>Fetch data!</button>
        </div>

    );
}

export default Card
