import React from 'react'
import 'weather-icons/css/weather-icons.css'
import styled from '@emotion/styled'
import ParseWeatherToIcon from '../functions/Parser'

const Container = styled.div`
    display: flex;
    min-width: 100%;
    flex-direction: column;
    align-items: center;
`

const WeatherIcon = styled.i`
    font-size: 50px;
`

const WeatherMaxMin = styled.div`
    display: flex;
    justify-content: space-between;
    font-size: 20px;
    min-width: 200px;
    margin-top: 20px;
`

const CardContent = ({ weather }) => {
    const icon = `{http://openweathermap.org/img/wn/${weather.icon}@2x.png}`;
    console.log('url', icon)
    return (
        <Container>
            <h2>{weather.title} in {weather.location}</h2>
            <img src={icon} alt={'img'}/>
            <WeatherMaxMin>
                <p>{weather.temp_max}ºC</p>
                <p>{weather.temp_min}ºC</p>
            </WeatherMaxMin>
        </Container>
    );
}

export default CardContent
