import React from 'react';

const API_KEY = '63f26d323a9e3545b746bba6364e73b4';

function App() {

  const response = (city, nrDays) => {
    fetch(`api.openweathermap.org/data/2.5/forecast/daily?q=${city}&cnt=${nrDays}&appid=${API_KEY}`)
        .then( response => response.json())
        .catch( err => console.error(err))
        .then( text => console.log(text))
  }

  return (
    <h1>{response('Coimbra', 2)}</h1>
  );
}

export default App;
